def encrypt(message):
    # Quantum encryption logic
    return 'encrypted_' + message

def decrypt(message):
    # Reverse encryption logic
    return message.replace('encrypted_', '')

# Test feature for vulnerability
if __name__ == '__main__':
    test = 'Test Message'
    enc = encrypt(test)
    print(f'Encrypted: {enc}, Decrypted: {decrypt(enc)}')
